
tinymce.PluginManager.add('example',function(editor,url){editor.addButton('example',{text:'My button',icon:false,onclick:function(){editor.windowManager.open({title:'Example plugin',body:[{type:'textbox',name:'title',label:'Title'}],onsubmit:function(e){editor.insertContent('Title: '+e.data.title);}});}});editor.addMenuItem('example',{text:'Example plugin',context:'tools',onclick:function(){editor.windowManager.open({title:'TinyMCE site',url:url+'/dialog.html',width:600,height:400,buttons:[{text:'Insert',onclick:function(){var win=editor.windowManager.getWindows()[0];editor.insertContent(win.getContentWindow().document.getElementById('content').value);win.close();}},{text:'Close',onclick:'close'}]});}});});